import React, {Component} from "react"
import { Form, Checkbox, Button, Header, Image, Modal } from 'semantic-ui-react'

class FormExampleCaptureValues extends Component {
  state = { name: '', days: '', steps: '', week_start_dates: '' }

  handleChange = (e, { name, value }) => this.setState({ [name]: value })

  handleSubmit = () => {
    const { name, days, steps, week_start_dates } = this.state

    this.setState({ submittedName: name, submittedEmail: email })
  }

  render() {
    const { name, days, steps, week_start_dates } = this.state

    return (
      <div>
        <Form onSubmit={this.handleSubmit}>
          <Form.Group>
            <Form.Input
              placeholder='Goal Name'
              name='name'
              value={name}
              onChange={this.handleChange}
            />
            <Form.Input
              placeholder='Tier 1 Session Headers'
              name='days'
              value={days}
              onChange={this.handleChange}
            />
            <Form.Input
              placeholder='Tier 2 Step Headers'
              name='steps'
              value={steps}
              onChange={this.handleChange}
            />
            <Form.Input
              placeholder='Weeks or Repetitions'
              name='week_start_dates'
              value={week_start_dates}
              onChange={this.handleChange}
            />
            <Form.Button content='Submit' />
          </Form.Group>
        </Form>
        <strong>onChange:</strong>
        <pre>{JSON.stringify({ name, days, steps, week_start_dates }, null, 2)}</pre>
        <strong>onSubmit:</strong>
        <pre>{JSON.stringify({ submittedName, submittedEmail }, null, 2)}</pre>
      </div>
    )
  }
}

export default FormExampleCaptureValues
