import React, {Component} from "react"
import { Form, Checkbox, Button, Header, Image, Modal } from 'semantic-ui-react'

class FormExampleCaptureValues extends Component {
  constructor(props) {
    super(props)
  }
  state = { name: this.props.goal.name, days: this.props.goal.days, steps: this.props.goal.categories, week_start_dates: this.props.goal.week_start_dates }

  handleChange = (e, { name, value }) => this.setState({ [name] : value.includes(',') ? value.split(',') : value })

  handleSubmit = (e) => {
    e.preventDefault()

    fetch(this.props.baseURL + '/goals', {
      method: 'PUT',
      body: JSON.stringify({
        name: this.state.name,

      }),
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => {
      return res.json()
    }).then(data => {
      this.props.editGoal(data)
      this.setState({
        name: '',
        type: '',
        days: '',
        week_start_dates: '',
        steps: ''
      })
    }).catch (error => console.error({'Error': error}))
  }

  render() {
    const { name, days, steps, week_start_dates, submittedName, submittedDays, submittedSteps, submittedWeek_start_dates } = this.state

    return (
      <div>
        <Form onSubmit={this.handleSubmit}>
          <Form.Group>
            <Form.Input
              label='Goal Name'
              name='name'
              value={name}
              onChange={this.handleChange}
            />

            <Form.Input
              label='Tier 1 Session Headers'
              name='days'
              value={days}
              onChange={this.handleChange}
            />
            <Form.Input
              label='Tier 2 Step Headers'
              name='steps'
              value={steps}
              onChange={this.handleChange}
            />
            <Form.Input
              label='Weeks or Repetitions'
              name='week_start_dates'
              value={week_start_dates}
              onChange={this.handleChange}
            />
            <Form.Button content='Submit' />
          </Form.Group>
        </Form>

      </div>
    )
  }
}

export default FormExampleCaptureValues
