import React, { Component } from 'react'
import { Form } from 'semantic-ui-react'

export default class Register extends Component {
  constructor(props) {
    super(props)
  }

  state = { name: '', username: '', password: ''}

  handleChange = (e, { name, value }) =>
    this.setState({ [name]: value })

  handleSubmit = () => {
    const { name, username, password } = this.state

    this.setState({ submittedName: name, submittedUsername: username, submittedPassword: password })
  }

  register = async (e) => {
  e.preventDefault()

  const { name, username, password } = this.state

  this.setState({ submittedName: name, submittedUsername: username, submittedPassword: password })

  console.log(this.state)


  const url = this.props.baseURL + '/users/signup'
  try {
    const response = await fetch(url, {
      method: 'POST',
      body: JSON.stringify({
        name: this.state.name,
        username: this.state.username,
        password: this.state.password
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    if (response.status === 200) {
      this.props.getGoals()
    }
  }
  catch (err) {
    console.log('Error => ', err);
  }
}

  render() {
    const { name, username, password, submittedName, submittedUsername, submittedPassword} = this.state

    return (
      <div>
        <Form onSubmit={this.register}>

            <Form.Input
              placeholder='Name'
              name='name'
              value={name}
              onChange={this.handleChange}
            />
            <Form.Input
              placeholder='Username'
              name='username'
              value={username}
              onChange={this.handleChange}
            />
            <Form.Input
              placeholder='Password'
              name='password'
              value={password}
              onChange={this.handleChange}
            />
            <Form.Button content='Register' />

        </Form>
        <strong>onChange:</strong>
        <pre>{JSON.stringify({ name, username, password }, null, 2)}</pre>
        <strong>onSubmit:</strong>
        <pre>{JSON.stringify({ submittedName, submittedUsername, submittedPassword }, null, 2)}</pre>
      </div>
    )
  }
}
